import pandas as pdimport numpy as npfrom datetime import datetime, timedeltanp.random.seed(42)num_sessions = 2500num_users = 500start_date = datetime(2025, 7, 3, 0, 0, 0)end_date = datetime(2025, 7, 12, 23, 59, 59)def random_timestamp(start, end):    delta = end - start    random_seconds = np.random.randint(0, int(delta.total_seconds()))    return start + timedelta(seconds=random_seconds)data = []session_id = 1for _ in range(num_sessions):    id_user = np.random.randint(1, num_users + 1)    open_time = random_timestamp(start_date, end_date)    duration_seconds = np.random.randint(60, 24 * 3600)    close_time = open_time + timedelta(seconds=duration_seconds)    if close_time > end_date:        close_time = end_date        duration_seconds = int((close_time - open_time).total_seconds())    data.append({        'id': session_id,        'id_user': id_user,        'action': 'open',        'date_action': open_time.strftime('%Y-%m-%d %H:%M:%S')    })    data.append({        'id': session_id,        'id_user': id_user,        'action': 'close',        'date_action': close_time.strftime('%Y-%m-%d %H:%M:%S')    })    session_id += 1df = pd.DataFrame(data)df = df.sample(frac=1, random_state=42).reset_index(drop=True)df.to_csv('users_sessions.csv', index=False)print(df.head(10))print(f"Загальна кількість рядків: {len(df)}")